INSERT INTO `faq` (`id`, `question`, `answer`, `position`) VALUES
(1, 'اینجا چه خبر است؟', 'اینجا « چل تیکه » است.\nفضایی که برای شما این امکان را ایجاد می کند که خودتان، بدون نیاز به دیگران، برای خودتان یک سایت پویا بسازید.\nیک سایت پویا یعنی سایتی که بدون نیاز به دست بردن در ساختار آن، بتوانید محتوا را به سادگی تغییر داده و تمام توانتان را روی کار اصلی خود متمرکز کنید.', 1),
(2, 'چرا از « چل تیکه » برای ساخت سایتم استفاده کنم؟', 'پاسخ این سوال بسیار ساده است.\nطراحی و ساخت یک وبسایت پویا نیاز به دانش فنی، صرف زمان و هزینه دارد. اما با استفاده از « چل تیکه » شما بدون نیاز به داشتن دانش فنی، با صرف تنها چند ساعت زمان و هزینه ای اندک می توانید صاحب یک وبسایت شوید.', 2),
(3, 'برای ساخت یک سایت با استفاده از چل تیکه به چه چیزهایی نیاز دارم؟', 'در حالتی که بخواهید به صورت معمول یک سایت طراحی کنید، با فرض در اختیار داشتن فضای میزبانی، تیم طراحی گرافیکی و تیم برنامه نویسی نیاز به مدیریت این تیم ها و البته صرف زمانی قابل توجه دارید.\nدر حالی که اگر با استفاده از « چل تیکه » به طراحی سایت خود بپردازید، با صرف تنها چند ساعت وقت، می توانید سایت خود را راه اندازی کنید. چرا که تیم « چل تیکه » با افتخار در خدمت شما هستند و تمام ابزارهای مورد نیاز شما برای طراحی و مدیریت سایتتان را در اختیارتان قرار داده اند. با استفاده از این ابزارها، شما بدون نیاز به داشتن دانش فنی، خود به تنهایی، کاری معادل یک تیم برنامه نویسی و یک تیم گرافیکی انجام می دهید.', 3),
(4, 'هزینه ی استفاده از « چل تیکه » چقدر است؟', 'هزینه ی استفاده از « چل تیکه » به دو بخش تقسیم می شود:\nیک بخش مربوط به حق اشتراک شما می باشد که بسته به طرح انتخاب شده توسط شما، طبق تعرفه ی نوشته شده در صفحه ی  <a style="color: rgba(0, 60, 170, 1); cursor: pointer;" onclick="open_tariff()"> تعرفه ها و تخفیف ها </a> محاسبه می گردد. این تعرفه ها، شامل هزینه ی فضای میزبانی، پهنای باند، استفاده ی یک ساله از « چل تیکه » و همچنین پشتیبانی گروه « چل تیکه » از شما می باشد.\nبخش دوم هزینه ای است که صرف خرید المان ها می شود. لازم به ذکر است که تنها تعدادی از المان ها پولی هستند و شما می توانید به سادگی از المان های رایگان برای طراحی و ساخت وبسایت خود استفاده کنید.', 4),
(5, 'چگونه می توانم اطمینان داشته باشم که « چل تیکه » کارایی لازم را برای رفع نیازهای من خواهد داشت؟', 'تیم « چل تیکه » پس از بحث و بررسی های مداوم به این نتیجه رسید که در جهت جلب اعتماد شما و جلوگیری از متضرر شدنتان، باید زمانی را در اختیار شما کاربران عزیز قرار دهد تا به انتخابی که کرده اید اعتماد نمایید.\nبر این اساس، شما از لحظه ی ثبت نام، <font style="color: rgba(0, 60, 170, 1);">30 روز</font> زمان دارید تا به صورت رایگان از امکانات « چل تیکه » استفاده کنید و قابلیت های آن را محک بزنید. پس از اتمام این زمان، وبسایت شما به صورت کامل حذف می گردد اما پیش از آن، چنانچه خدمات « چل تیکه » برای شما رضایتبخش بود، می توانید با خرید یکی از طرح های موجود در صفحه ی  <a style="color: rgba(0, 60, 170, 1); cursor: pointer;" onclick="open_tariff()"> تعرفه ها و تخفیف ها</a>، وبسایت خود را برای یک سال ثبت نمایید.\nلازم به ذکر است که استفاده رایگان شما از خدمات « چل تیکه » در این مدت <font style="color: rgba(0, 60, 170, 1);">30 روز</font> شامل استفاده ی رایگان از المان های غیر رایگان <font style="color: rgba(0, 60, 170, 1);">نمی باشد</font>.', 5),
(6, 'در صورت بروز مشکل یا نیاز به طرح سوال چه کنم؟', 'شما به سادگی می توانید، در هر ساعت از شبانه روز با ورود به قسمت تنظیمات وبسایت خود در با ارسال یک تیکت از زیر منوی <font style="color: rgba(0, 60, 170, 1);">طرح مشکل با پشتیبانی</font>، مشکل یا سوال خود را با تیم پشتیبانی « چل تیکه » مطرح کنید.\n<font style="color: rgba(0, 60, 170, 1);">تیم پشتیبانی در اولین فرصت جهت رفع مشکل، با شما تماس خواهد گرفت.</font>', 6),
(7, 'چطور باید از « چل تیکه » استفاده کنم؟ ', 'بدون شک، به سادگی...\nتیم طراحی « چل تیکه »، تمام تلاش خود را کرده است که آن را سازگار با پیش فرض های ذهنی کاربران طراحی کند. بنا بر این طرز کار با « چل تیکه » را بدون نیاز به آموزش و تنها با تکیه بر دانسته های پیشین خود تا حد قابل قبولی می دانید.\nاما با این وجود، تیم « چل تیکه » برای اطمینان از آسایش و راحتی کاربران، صفحه ی <font style="color: rgba(0, 60, 170, 1); cursor: pointer;" onclick="open_learning();">آموزش</font> را طراحی و راهنمای استفاده از « چل تیکه » را در آن قرار داده است.\nشما با استفاده از این آموزش ها، می توانید به سادگی و در زمانی کوتاه کار با « چل تیکه » را به طور کامل بیاموزید.', 7);

